/*==================================================
src/components/Credits.js

The Credits component contains information for Credits page view.
Note: You need to work on this file for the Assignment.
==================================================*/
import {Link} from 'react-router-dom';
import React, {useState} from 'react'

const Credits = ({credits, accountBalance, setCredits, setAccountBalance}) => {

  //hooks to use for setCredits and setDescription
  const [description, setDescription] = useState(''); 
  const [amount, setAmount] = useState(''); 

  //handles event when user clicks on add credit
  const addCredit = () => 
  { 
    let newCredit = { 
      id: credits.length + 1, 
      description: description, 
      amount: parseFloat(amount).toFixed(2), 
      date: new Date().toISOString().split('T')[0] // Format as yyyy-mm-dd 
      }; 

    
    //adding a new debit item to the end of the list
    setCredits([...credits, newCredit]); 
    //updates balance to reflect the new item being added
    setAccountBalance((parseFloat(accountBalance) + parseFloat(newCredit.amount)).toFixed(2)); 

    //resets to blank
    setDescription(''); 
    setAmount(''); 
  };

  //displays list of credits with a form for user to add a new credit
  return (
    <div>
      <h1>Credits</h1>
      <h2> Account Balance: ${accountBalance}</h2>

      <form onSubmit={ (e) => {e.preventDefault(); addCredit(); }}>
        <div>
          <label>Description: </label>
          <input
            type = 'text'
            value = {description}
            onChange = {(e) =>setDescription(e.target.value)}
            required
          />
        </div>
        <div>
          <label> Amount: </label>
          <input
            type = "number"
            step = "0.01"
            value = {amount}
            onChange = {(e) => setAmount (e.target.value)}
            required
          />
        </div>
        <button type = "submit">Add Credit</button>
      </form>
      <ul>
        {credits.map((credit) => (
          <li key = {credit.id}>
            <div>
              <strong> Description: </strong> {credit.description}
            </div>
            <div>
              <strong>Amount: </strong> ${credit.amount}
            </div>
            <div>
              <strong>Date: </strong> {new Date(credit.date).toLocaleString()}
            </div>
            <hr/>
          </li>
        ))}
      </ul>
      <Link to="/">Return to Home</Link>
    </div>
  );
}

export default Credits;











